con:configuration.scriptText:
// validate AES encription parameters in response, if exist.

def encAES= context.expand( '${110P_Autorisation-AllParams#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/unboundField[@index=110]/@value}' )
def macAES = context.expand( '${110P_Autorisation-AllParams#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/unboundField[@index=128]/@value}' )


if (encAES!=null && encAES.toString().length()>0)
{
	encAES = context.expand( '${110P_Autorisation-AllParams#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/rawData[1]}' )
	String partAES=encAES.toString().substring(238)
	assert partAES.contains('8210') && partAES.contains('8402')
	 log.info( partAES.toString())
}

if (macAES!=null && macAES.toString().length()>0)
	assert macAES.toString().length()==16

// validate AES encription parameters in response, if exist.
def encAES = context.expand( '${200N_Storno-AllParams-Error-2F#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/unboundField[@index=110]/@value}' )
def macAES = context.expand( '${200N_Storno-AllParams-Error-2F#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/unboundField[@index=128]/@value}' )


if (encAES!=null && encAES.toString().length()>0)
{
	encAES = context.expand( '${200N_Storno-AllParams-Error-2F#Response#declare namespace ns2=\'http://directrecharge.o2.com/iso8583/\'; //ns2:opalResponse[1]/rawData[1]}' )
	String partAES=encAES.toString().substring(238)
	assert partAES.contains('8210') && partAES.contains('8402')
	 log.info( partAES.toString())
}

if (macAES!=null && macAES.toString().length()>0)
	assert macAES.toString().length()==16

