con:config.script:
/* * This script replace bank data from tenant in generated camt054 file for credit based on US-138 * Author: Michael Gordon * Date: 01.02.2017 */
// set reguired values from properties String sFilePath = context.expand( '${#TestCase#outFilePath}' ) String sTenant1_IBAN = context.expand( '${#Project#ChargeCustomer-1-1-IBAN}' ) String sTenant2_IBAN = context.expand( '${#Project#ChargeCustomer-2-1-IBAN}')
// create file for read and write in created camt054 file credit File fFile=new File(sFilePath) assert fFile.exists()
// replace iban from tenant 1 with iban from tenant 2 in camt054-credit file String sContent=new File(sFilePath).getText('utf-8') log.info('/n'+ sContent+ '/n') fFile.write( sContent.replaceAll( sTenant1_IBAN, sTenant2_IBAN ), 'utf-8')
log.info('Replaced IBAN: ' +sTenant1_IBAN + ' with ' +sTenant2_IBAN + ' in file: '+ sFilePath)
