con:config.script:
/*
 * This script sets the MAXID values in the Testuite property. If the MAXID is null, the MAXID property value is set as '0'
  * Date: 2021-09-02 
 * Autor: Vinay Goli
*/
//Get Count & MAXID for CAMT & TASK tables from responses of Test Steps: JDBC Request-getMaxID-CAMT05400102_INPUT_ENTRY & JDBC Request-getMaxID-TASK

String CountTask = context.expand( '${JDBC Request-getMaxID-TASK#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/COUNTID[1]}' )
String CountCamt = context.expand( '${JDBC Request-getMaxID-CAMT05400102_INPUT_ENTRY#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/COUNTID[1]}' )
def MAXIDCAMT = context.expand( '${JDBC Request-getMaxID-CAMT05400102_INPUT_ENTRY#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/MAXID[1]}' )
def MAXIDTASK = context.expand( '${JDBC Request-getMaxID-TASK#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/MAXID[1]/text()}' )

//log Count of rows
log.info(CountTask)
log.info(CountCamt)


// Conditional statement to set MAXID as 0 if there are no records in TASK table, else set the MAXID
if (CountTask.toInteger() == 0 )


{
		testRunner.testCase.testSuite.setPropertyValue('MAXIDTASK','0')
}
else 
{
		testRunner.testCase.testSuite.setPropertyValue('MAXIDTASK',MAXIDTASK)
}

// Conditional statement to set MAXID as 0 if there are no records in CAMT05400102_INPUT_ENTRY table, else set the MAXID
if (CountCamt.toInteger() == 0)
{		
	testRunner.testCase.testSuite.setPropertyValue('MAXIDCAMT','0')
}
else 
{		
		testRunner.testCase.testSuite.setPropertyValue('MAXIDCAMT',MAXIDCAMT)
}

//Create TesSuite Parameters for MAXID_After and set current MAXID values.
testRunner.testCase.testSuite.setPropertyValue('MAXIDCAMT_After',MAXIDCAMT)
testRunner.testCase.testSuite.setPropertyValue('MAXIDTASK_After',MAXIDTASK)
