con:config.script:
/* * Save pathes from files in test-case properties * Autor: Michael Gordon * Date: 18.12.2019 */
// read pathes
String outPath_DAT = context.expand( '${Run TestCase_CreateLoadDAT#outPath}' ) String outPath_QIT = context.expand( '${Run TestCase_CreateLoadQIT#outPath}' ) String sNr = context.expand( '${DataSource-createFiles#Nr}' )
// set properties testRunner.testCase.setPropertyValue('DATPath'+sNr,outPath_DAT) testRunner.testCase.setPropertyValue('QITPath'+sNr,outPath_QIT)

/*
 *  This script validate count of task processed with jobs in step before
 *  Autor : Michael Gordon
 *  Date:  10.12.2019
 */

// read count of tasks returned in step before
def taskCountDownloadDat = context.expand( '${Run TestCase-ExecJobs-again#taskCount-DownloadDat}' ).toInteger()
def taskCountUploadDat = context.expand( '${Run TestCase-ExecJobs-again#taskCount-UploadDat}' ).toInteger()
def taskCountDownloadQIT = context.expand( '${Run TestCase-ExecJobs-again#taskCount-DownloadQIT}' ).toInteger()
def taskCountUploadQIT = context.expand( '${Run TestCase-ExecJobs-again#taskCount-UploadQIT}' ).toInteger()
def taskCountReport = context.expand( '${Run TestCase-ExecJobs-again#taskCount-Report}' ).toInteger()

// validate
assert taskCountDownloadDat==4
assert taskCountUploadDat==0
assert  taskCountDownloadQIT>=4
assert taskCountUploadQIT==0
assert taskCountReport==0
