 // Generate msisdn in format for expected behavior from mock services
  String PrefixPiranha = context.expand( '${#Project#MOCK-Prefix-Piranha-StackService-CustomResponse}' )
 String sMSISDN_Unknown=PrefixPiranha+'99'+context.expand( '${#Project#Random5}' ) 

 // get known ibans
String[] sIBANS  = context.expand( '${getSettingChildNodes#Response#declare namespace ns2=\'http://directrecharge.o2.com/ws/common/settings/v1/\'; //ns2:getSettingChildNodesResponse[1]/parentEntry[1]/value[1]}' ).split(';')


 // write msisdns in properties
 testRunner.testCase.setPropertyValue('MSISDN-Unknown',sMSISDN_Unknown)
 // write ibans in properties
 testRunner.testCase.setPropertyValue('IBAN-1',sIBANS[0] )
  testRunner.testCase.setPropertyValue('IBAN-2', sIBANS[1] )
