con:config.script:
/*
 * This script generate msisdns and contract id for errors by ocs deduct and ppit mocks
 * Date: 28.05.2019
 * Autor: Michael Gordon
*/

 // read msisdn prefixes for custom responses from mocks
 String PrefixOCS10deduct = context.expand( '${#Project#MOCK-Prefix-OCS10-deductBalance-CustomException}' )
 String  mOCKPrefixNgOCSajustment = context.expand( '${#Project#MOCK-Prefix-NgOCS-ajustment-CustomException}' )
 String  oCSSetting_NEW = context.expand( '${#Project#OCSSetting_NEW}' )
 
  String PrefixPPITCustomResponse = context.expand( '${#Project#MOCK-Prefix-PPIT-CustomCareService-CustomResponse}' )
 String sCurrentSetting = context.expand( '${_Run TestCase-getSetting#outSetting}' )
 
 // generate msisdns for ocs-error for deduct  
 String msisdn91=PrefixOCS10deduct+'100000091'
 String msisdn98800006=PrefixOCS10deduct+'198800006'
 // Generate contractID for PPIT-Clawback-Error=471
 String contractID=PrefixPPITCustomResponse+'01471'+context.expand( '${#Project#Random8}' )

 // rewrite msisdns with the same errors for ngOCS.ajustment, if ocs-setting=ngocs.
 if (sCurrentSetting==oCSSetting_NEW){
	msisdn91=mOCKPrefixNgOCSajustment+'902050199'
	msisdn98800006=mOCKPrefixNgOCSajustment+'902050252'
 }
 
 
 // write in properties
 testRunner.testCase.setPropertyValue('MSISDN-OCS-Error-91',msisdn91)
 testRunner.testCase.setPropertyValue('MSISDN-OCS-Error-98800006',msisdn98800006)
 testRunner.testCase.setPropertyValue('ContractID-PPIT-Error', contractID)
