con:config.script:
/* * This script generate test-data and set in test-cases property * Date: 16.09.2019 * Autor: Michael Gordon */
// Generate msisdn in format for expected behavior from mock services String sMSISDN_OK = context.expand( '${#Project#MSISDN-chargeCustomer-OK}' ) String PrefixPiranha = context.expand( '${#Project#MOCK-Prefix-Piranha-StackService-CustomResponse}' ) String sMSISDN_Unknown=PrefixPiranha+'99'+context.expand( '${#Project#Random5}' )
// get known ibans String[] sIBANS = context.expand( '${getSettingChildNodes#Response#declare namespace ns2=\'http://directrecharge.o2.com/ws/common/settings/v1/\'; //ns2:getSettingChildNodesResponse[1]/parentEntry[1]/value[1]}' ).split(';')
// write msisdns in properties testRunner.testCase.setPropertyValue('MSISDN-OK', sMSISDN_OK ) testRunner.testCase.setPropertyValue('MSISDN-Unknown',sMSISDN_Unknown) // write ibans in properties testRunner.testCase.setPropertyValue('IBAN-1',sIBANS[0] ) testRunner.testCase.setPropertyValue('IBAN-2', sIBANS[1] )

/* * Set file name in format for bank transfer: Ex.: 2019-09-11_C54_DE73700202700005716977_EUR_000510.xml * Date: 16.09.2019 * Autor: Michael Gordon */
// set required data for new file name String sPath = context.expand( '${Run TestCase-splitPath#outPath}' ) String sCurrentDate = context.expand( '${#Project#Date_Current_yyyy-MM-dd}' ) String sRrandom6 = context.expand( '${#Project#Random6}' ) String sCurrentIBAN = context.expand( '${DataSource-getIBANs#IBAN-1}' )
// create new file name String sFileName=sCurrentDate+'_C54_'+sCurrentIBAN+'_EUR_'+ sRrandom6+'.xml' String sFilePath=(sPath +'/'+sFileName) log.info( 'New file name is: - '+ sFileName) log.info( 'New path is: - ' + sFilePath) // set value in property testRunner.testCase.setPropertyValue('FilePathNew', sFilePath)

/* * Set file name in format for bank transfer: Ex.: 2019-09-11_C54_DE73700202700005716977_EUR_000510.xml * Date: 16.09.2019 * Autor: Michael Gordon */
// set required data for new file name String sPath = context.expand( '${Run TestCase-splitPath-1#outPath}' ) String sCurrentDate = context.expand( '${#Project#Date_Current_yyyy-MM-dd}' ) String sRrandom6 = context.expand( '${#Project#Random6}' ) String sCurrentIBAN = context.expand( '${#TestCase#IBAN-1}' )
// create new file name String sFileName=sCurrentDate+'_C54_'+sCurrentIBAN+'_EUR_'+ sRrandom6+'.xml' String sFilePath=(sPath +'/'+sFileName) log.info( 'New file name is: - '+ sFileName) log.info( 'New path is: - ' + sFilePath) // set value in property testRunner.testCase.setPropertyValue('FilePathNew', sFilePath)
