// read table name and old count 
String sTableName = context.expand( '${DataSource-getTableCounts#tableName}' )
String sDiffCount =context.expand( '${DataSource-getTableCounts#diffCount}' )
String sCount= context.expand( '${JDBC Request-getCount#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/COUNT[1]}' )
 // log.info(sTableName +' - '+ sCount  +' - '+  sDiffCount )

 // compare counts
  log.info('Table: -  '+sTableName +'  Count: - '+sCount+' expected count is : - '+sDiffCount )
 assert sCount.toInteger()==sDiffCount.toInteger()
